import React, { useState } from 'react';
import { ethers } from 'ethers';
import MusicNFT from './MusicNFT.json';

const contractAddress = '0xe37cB402D08CE22D340Ef413bFb5666384488339'; // Your address

function App() {
  const [tokenId, setTokenId] = useState('');
  const [chain, setChain] = useState([]);
  const [metadata, setMetadata] = useState(null);

  const getNFTData = async () => {
    try {
      const provider = new ethers.providers.Web3Provider(window.ethereum);
      await provider.send('eth_requestAccounts', []);
      const contract = new ethers.Contract(contractAddress, MusicNFT.abi, provider);
      const history = await contract.getOwnershipHistory(tokenId);
      setChain(history);
      const uri = await contract.tokenURI(tokenId);
      const response = await fetch(`https://ipfs.io/ipfs/${uri.split('ipfs://')[1]}`);
      const meta = await response.json();
      setMetadata(meta);
    } catch (error) {
      console.error('Error fetching NFT data:', error);
      alert('Error: Check console.');
    }
  };

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial' }}>
      <h1>IP Management Lookup</h1>
      <input
        type="text"
        placeholder="Enter Token ID (e.g., 790)"
        value={tokenId}
        onChange={(e) => setTokenId(e.target.value)}
        style={{ padding: '5px', marginRight: '10px' }}
      />
      <button onClick={getNFTData} style={{ padding: '5px 10px' }}>Check</button>
      {chain.length > 0 && (
        <div>
          <h2>Chain of Custody</h2>
          <ul>
            {chain.map((owner, index) => (
              <li key={index}>{owner}</li>
            ))}
          </ul>
        </div>
      )}
      {metadata && (
        <div>
          <h2>Metadata</h2>
          <p><strong>Name:</strong> {metadata.name}</p>
          <p><strong>Reference:</strong> {metadata.reference}</p>
          <ul>
            {metadata.attributes.map((attr, index) => (
              <li key={index}>{`${attr.trait_type}: ${attr.value}`}</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default App;
