import React, { useState } from 'react';
import { ethers } from 'ethers';
import MusicNFT from './MusicNFT.json';

const contractAddress = '0xABC123...'; // Your address

function App() {
  const [tokenId, setTokenId] = useState('');
  const [chain, setChain] = useState([]);
  const [metadata, setMetadata] = useState(null);

  const getNFTData = async () => {
    try {
      console.log('Connecting to provider...');
      const provider = new ethers.providers.Web3Provider(window.ethereum);
      await provider.send('eth_requestAccounts', []);
      console.log('Provider connected:', provider);
      const contract = new ethers.Contract(contractAddress, MusicNFT.abi, provider);
      console.log('Contract created:', contract.address);

      console.log('Fetching history for token:', tokenId);
      const tokenIdNum = parseInt(tokenId, 10);
      console.log('Parsed token ID:', tokenIdNum);
      const history = await contract.getOwnershipHistory(tokenIdNum);
      console.log('History:', history);
      setChain(history);

      console.log('Fetching URI...');
      const uri = await contract.tokenURI(tokenIdNum);
      console.log('URI:', uri);
      const response = await fetch(`https://ipfs.io/ipfs/${uri.split('ipfs://')[1]}`);
      const meta = await response.json();
      console.log('Metadata:', meta);
      setMetadata(meta);
    } catch (error) {
      console.error('Error fetching NFT data:', error);
      alert('Error: Check console for details.');
    }
  };

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial' }}>
      <h1>IP Management Lookup</h1>
      <input
        type="text"
        placeholder="Enter Token ID (e.g., 790)"
        value={tokenId}
        onChange={(e) => setTokenId(e.target.value)}
        style={{ padding: '5px', marginRight: '10px' }}
      />
      <button onClick={getNFTData} style={{ padding: '5px 10px' }}>Check</button>
      {chain.length > 0 && (
        <div>
          <h2>Chain of Custody</h2>
          <ul>
            {chain.map((owner, index) => (
              <li key={index}>{owner}</li>
            ))}
          </ul>
        </div>
      )}
      {metadata && (
        <div>
          <h2>Metadata</h2>
          <p><strong>Name:</strong> {metadata.name || 'N/A'}</p>
          <p><strong>Reference:</strong> {metadata.reference || 'N/A'}</p>
          {metadata.attributes && metadata.attributes.length > 0 ? (
            <ul>
              {metadata.attributes.map((attr, index) => (
                <li key={index}>{`${attr.trait_type}: ${attr.value}`}</li>
              ))}
            </ul>
          ) : (
            <p>No attributes available</p>
          )}
        </div>
      )}
    </div>
  );
}

export default App;
